local p=game.Players.LocalPlayer local hrp=p.Character and p.Character:FindFirstChild("HumanoidRootPart") if not hrp then return end local TS=game:GetService("TeleportService") local HS=game:GetService("HttpService") local fs=writefile and readfile and writefile local fn="candyfarm.txt" local maxHist=5 local url="https://games.roblox.com/v1/games/"..game.PlaceId.."/servers/Public?sortOrder=Asc&limit=100" local curJob=game.JobId local function loadHist() if not fs or not isfile(fn) then if fs then writefile(fn,"") end return{} end local c=readfile(fn) if not c or c=="" then return{} end local t={} for line in c:gmatch("[^\r\n]+") do table.insert(t,line) end return t end local function saveHist(t) while#t>maxHist do table.remove(t) end writefile(fn,table.concat(t,"\n")) end local hist=loadHist() table.insert(hist,1,curJob) saveHist(hist) for _,house in ipairs(workspace.Houses:GetChildren()) do local d=house:FindFirstChild("Door") if d then local dt=d:FindFirstChild("DoorInnerTouch") if dt then for _,ti in ipairs(dt:GetChildren()) do if ti:IsA("TouchTransmitter") then firetouchinterest(hrp,ti.Parent,0) firetouchinterest(hrp,ti.Parent,1) end end end end end task.wait(4) local function getServers() while true do local s,res=pcall(function() return game:HttpGet(url) end) if s and res then local data=HS:JSONDecode(res) if data and data.data and #data.data>0 then return data.data end end task.wait(4) end end local servers=getServers() local small={} local exclude={} for _,id in ipairs(hist) do exclude[id]=true end for _,v in ipairs(servers) do if v.id~=curJob and v.playing<10 and not exclude[v.id] then table.insert(small,v) end end if#small>0 then local srv=small[math.random(1,#small)] table.insert(hist,1,srv.id) saveHist(hist) TS:TeleportToPlaceInstance(game.PlaceId,srv.id,p) end task.spawn(function() task.wait(30) if true then loadstring(string.dump(script))() end end)
